import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const HomeScreen = () => {
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>BLURB</Text>
      </View>
      <View style={styles.textContainer}>
        <Text style={styles.text}>in Front of</Text>
        <Text style={styles.text}>Background</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'flex-start',
  },
  header: {
    paddingTop: 20, // Adjust the paddingTop as needed to create space for the header
    backgroundColor: '#white',
  },
  headerText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
  },
  textContainer: {
    flex: 1,
    justifyContent: 'flex-start',
    alignItems: 'center', // Align items at the top of the container
    paddingTop: 20, // Adjust the paddingTop as needed to create space between header and text
  },
  text: {
    color: 'black',
    fontSize: 42,
    textAlign: 'center',
  },
});

export default HomeScreen;
