import React, { useState } from 'react';
import { View, Button, Text, TextInput, TouchableOpacity, CheckBox, ScrollView, StyleSheet } from 'react-native';

const SurveyPage = ({ navigation }) => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [isUnder18, setIsUnder18] = useState(false);
  const [occupation, setOccupation] = useState('');
  const [income, setIncome] = useState('');
  const [showResults, setShowResults] = useState(false);

  const handleSurveySubmit = () => {
    // Logic to handle the survey submission and show results
    setShowResults(true);
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.header}>Tax Survey</Text>

      <View style={styles.checkboxContainer}>
        <CheckBox
          value={isUnder18}
          onValueChange={value => setIsUnder18(value)}
        />
        <Text>Are you under 18?</Text>
      </View>

      {isUnder18 ? (
        <View>
          <Text>Dependent stuff</Text>
        </View>
      ) : (
        <View>
          <Text style={styles.label}>Occupation:</Text>
          <View style={styles.checkboxContainer}>
            <CheckBox
              value={occupation === 'student'}
              onValueChange={() => setOccupation('student')}
            />
            <Text>Student</Text>
          </View>
          <View style={styles.checkboxContainer}>
            <CheckBox
              value={occupation === 'other'}
              onValueChange={() => setOccupation('other')}
            />
            <Text>Other</Text>
          </View>

          {occupation === 'student' && (
            <View>
              <Text>Ask about student loans</Text>
            </View>
          )}

          {occupation === 'other' && (
            <View>
              <Text>Display general info for other occupation</Text>
            </View>
          )}

          {occupation === 'employed' && (
            <View>
              <Text style={styles.label}>Income:</Text>
              <View style={styles.checkboxContainer}>
                <CheckBox
                  value={income === 'overCertainAmount'}
                  onValueChange={() => setIncome('overCertainAmount')}
                />
                <Text>Income over a certain amount</Text>
              </View>
              <View style={styles.checkboxContainer}>
                <CheckBox
                  value={income === 'underCertainAmount'}
                  onValueChange={() => setIncome('underCertainAmount')}
                />
                <Text>Income under a certain amount</Text>
              </View>
              {income === 'overCertainAmount' && (
                <Text>Display information for income over a certain amount</Text>
              )}
              {income === 'underCertainAmount' && (
                <View>
                  <Text>Display information for income under a certain amount</Text>
                  <Text>Single under 65: $12,950</Text>
                </View>
              )}
            </View>
          )}
        </View>
      )}

      <TouchableOpacity style={styles.button} onPress={handleSurveySubmit}>
        <Text style={styles.buttonText}>Submit Survey</Text>
      </TouchableOpacity>

      {showResults && (
        <View>
          {/* Display tax filing obligation message + resources - whatever else we want */}
        </View>
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    padding: 16,
    justifyContent: 'center',
    alignItems: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  checkboxContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  button: {
    backgroundColor: '#007bff',
    paddingVertical: 12,
    paddingHorizontal: 20,
    borderRadius: 8,
    marginTop: 20,
  },
  buttonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});

export default SurveyPage;
