import React, { useState } from 'react';
import { View, Text, StyleSheet, ImageBackground, Button } from 'react-native';

const image = require('../assets/library.jpg');

const LinksPage = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
        <View style={styles.nav}>
          <Button
            title="Survey"
            color="black"
            onPress={() => navigation.navigate('Survey')}
          />
          <Button
            title="Information"
            color="black"
            onPress={() => navigation.navigate('Information')}
          />
          <Button
            title="Tax Fraud"
            color="black"
            onPress={() => navigation.navigate('TaxFraud')}
          />
          <Button
            title="About Us"
            color="black"
            onPress={() => navigation.navigate('AboutUs')}
          />
        </View>
        <View style={styles.header}>
          <Text style={styles.headerText}>Find Basic Information on Taxes Below</Text>
        </View>
        <>
          <Info name="1" />
          <Info name="2" />
          <Info name="3" />
          <Info name="4" />
          <Info name="5" />
          <Info name="6" />
          <Info name="7" />
          <Info name="8" />
          <Info name="9" />
        </>
      </ImageBackground>
    </View>
  );
};

const Info = (props) => {
  const [toScreen, setToScreen] = useState(true);

  return (
    <View style={styles.infoContainer}>
      <Button
        style={styles.button}
        onPress={() => {
          setToScreen(false);
        }}
        disabled={!toScreen}
        title={toScreen ? props.name : 'Read More'}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'flex-start',
  },
  image: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
  },
  nav: {
    flex: 0.2,
    justifyContent: 'center',
    flexDirection: 'row',
    alignItems: 'flex-start',
  },
  header: {
    paddingTop: 10,
  },
  headerText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    color: 'white',
  },
  infoContainer: {
    paddingTop: 20,
    justifyContent: 'center',
    alignItems: 'center',
  },
  button: {
    paddingVertical: 10,
    paddingHorizontal: 20,
    marginBottom: 10,
    backgroundColor: 'black',
    color: 'white',
  },
});

export default LinksPage;
