import React from 'react';
import { View, Button, Text, TextInput, TouchableOpacity, CheckBox, ScrollView, StyleSheet } from 'react-native';
import { Formik } from 'formik';

const SurveyPage = ({ navigation }) => {
  const handleSurveySubmit = (values) => {
    // Logic to handle the survey submission and show results
    console.log(values);
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.header}>Tax Survey</Text>

      <Formik
        initialValues={{
          name: '',
          age: '',
          occupation: '',
          studentLoans: false,
          income: '',
        }}
        onSubmit={handleSurveySubmit}
      >
        {({ handleChange, handleBlur, handleSubmit, values }) => (
          <View>
            <Text style={styles.label}>Name:</Text>
            <TextInput
              style={styles.input}
              placeholder="Please enter your name"
              onChangeText={handleChange('name')}
              onBlur={handleBlur('name')}
              value={values.name}
            />

            <Text style={styles.label}>Age:</Text>
            <TextInput
              style={styles.input}
              placeholder="Please enter your age"
              onChangeText={handleChange('age')}
              onBlur={handleBlur('age')}
              value={values.age}
              keyboardType="numeric"
            />

            <View style={styles.checkboxContainer}>
              <CheckBox
                value={values.isUnder18}
                onValueChange={handleChange('isUnder18')}
              />
              <Text>Are you under 18?</Text>
            </View>

            {values.isUnder18 ? null : (
              <View>
                <Text style={styles.label}>Occupation:</Text>
                <View style={styles.checkboxContainer}>
                  <CheckBox
                    value={values.occupation === 'student'}
                    onValueChange={() => handleChange('occupation')('student')}
                  />
                  <Text>Student</Text>
                </View>
                <View style={styles.checkboxContainer}>
                  <CheckBox
                    value={values.occupation === 'other'}
                    onValueChange={() => handleChange('occupation')('other')}
                  />
                  <Text>Other</Text>
                </View>

                {values.occupation === 'student' && (
                  <View>
                    {/* Ask about student loans */}
                  </View>
                )}

                {values.occupation === 'other' && (
                  <View>
                    {/* Display general info for other occupation */}
                  </View>
                )}

                {values.occupation === 'employed' && (
                  <View>
                    <Text style={styles.label}>Income:</Text>
                    {/* Display income-related checkboxes */}
                  </View>
                )}
              </View>
            )}

            <TouchableOpacity style={styles.button} onPress={handleSubmit}>
              <Text style={styles.buttonText}>Submit Survey</Text>
            </TouchableOpacity>
          </View>
        )}
      </Formik>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    padding: 16,
    justifyContent: 'center',
    alignItems: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  input: {
    height: 40,
    width: '100%',
    borderColor: '#ccc',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
    borderRadius: 5,
  },
  checkboxContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  button: {
    backgroundColor: '#007bff',
    paddingVertical: 12,
    paddingHorizontal: 20,
    borderRadius: 8,
    marginTop: 20,
  },
  buttonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});

export default SurveyPage;
