import React, { useState } from 'react';
import { View, Text, CheckBox, ScrollView, StyleSheet } from 'react-native';

const SurveyPage = ({ navigation }) => {
  const [isUnder18, setIsUnder18] = useState(false);
  const [occupation, setOccupation] = useState('');
  const [income, setIncome] = useState('');
  const [filingStatus, setFilingStatus] = useState('');
  const [showResults, setShowResults] = useState(false);

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.header}>Tax Survey</Text>

      <View style={styles.checkboxContainer}>
        <CheckBox
          value={isUnder18}
          onValueChange={value => setIsUnder18(value)}
        />
        <Text>Are you under 18?</Text>
      </View>

      {isUnder18 ? (
        <View>
          <Text>Dependent stuff</Text>
        </View>
      ) : (
        <View style={styles.centerContent}>
          <Text style={styles.label}>Occupation:</Text>
          <View style={styles.checkboxContainer}>
            <CheckBox
              value={occupation === 'student'}
              onValueChange={() => setOccupation('student')}
            />
            <Text>Student</Text>
          </View>
          <View style={styles.checkboxContainer}>
            <CheckBox
              value={occupation === 'other'}
              onValueChange={() => setOccupation('other')}
            />
            <Text>Other</Text>
          </View>
          <View style={styles.checkboxContainer}>
            <CheckBox
              value={occupation === 'employed'}
              onValueChange={() => setOccupation('employed')}
            />
            <Text>Employed</Text>
          </View>

          {occupation === 'student' && (
            <View>
              <Text>Ask about student loans</Text>
            </View>
          )}

          {occupation === 'other' && (
            <View>
              <Text>Display general info for other occupation</Text>
            </View>
          )}

          {occupation === 'employed' && (
            <View>
              <Text style={styles.label}>Filing Status:</Text>
              <View style={styles.checkboxContainer}>
                <CheckBox
                  value={filingStatus === 'single'}
                  onValueChange={() => setFilingStatus('single')}
                />
                <Text>Single</Text>
              </View>
              <View style={styles.checkboxContainer}>
                <CheckBox
                  value={filingStatus === 'married'}
                  onValueChange={() => setFilingStatus('married')}
                />
                <Text>Married</Text>
              </View>
              {filingStatus === 'single' && (
                <View>
                  <Text style={styles.label}>Income:</Text>
                  <View style={styles.checkboxContainer}>
                    <CheckBox
                      value={income === 'underSingle65'}
                      onValueChange={() => setIncome('underSingle65')}
                    />
                    <Text>Single under 65: $12,950</Text>
                  </View>
                  <View style={styles.checkboxContainer}>
                    <CheckBox
                      value={income === 'aboveSingle65'}
                      onValueChange={() => setIncome('aboveSingle65')}
                    />
                    <Text>Single 65 or older: $14,700</Text>
                  </View>
                </View>
              )}
              {filingStatus === 'married' && (
                <View>
                  {/* Display income options for married filing jointly */}
                </View>
              )}
            </View>
          )}
        </View>
      )}

      {showResults && (
        <View>
          {/* Display tax filing obligation message + resources - whatever else we want */}
        </View>
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#f4f4f4', // Added background color
  },
  centerContent: {
    alignItems: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    textAlign: 'center',
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  checkboxContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
});

export default SurveyPage;
