import React from 'react';
import { View, Text, StyleSheet, ImageBackground, Button, Box } from 'react-native';
import Constants from 'expo-constants';

const image = require('../assets/library.jpg');

const LinksPage = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
        <View style={styles.nav}>
          <Button
            title="Survey"
            color="black"
            onPress={() => navigation.navigate('Survey')}
          />
          <Button
            title="Tax Forms"
            color="black"
            onPress={() => navigation.navigate('TaxForms')}
          />
          <Button
            title="Tax Fraud"
            color="black"
            onPress={() => navigation.navigate('TaxFraud')}
          />
          <Button
            title="About Us"
            color="black"
            onPress={() => navigation.navigate('AboutUs')}
          />
        </View>
        <View style={styles.header}>
          <Text style={styles.headerText}>Find basic information on taxes before. </Text>
        </View>
        <Text style={styles.text}>Finish survey to see what BOX is best for you.</Text>
        <Box
          style={{
            backgroundColor: '#333',
            borderRadius: 4,
            color: '#eee',
            minHeight: 200,
            padding: 12,
            width: 300,
      }}
    >
      Sally Montgomery
    </Box>
      </ImageBackground>
    </View>
  );
};


const styles = StyleSheet.create({
  nav: {
    flex: 0.2,
    justifyContent: 'center',
    flexDirection: 'row',
    alignItems: 'flex-start',
  },
  container: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'flex-start',
  },
  image: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
  },
  header: {
    paddingTop: 10,
  },
  headerText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    color: 'white',
  },
  textContainer: {
    flex: 1,
    justifyContent: 'flex-start',
    alignItems: 'center',
    paddingTop: 20,
  },
  text: {
    color: 'white',
    fontSize: 16,
    textAlign: 'center',
  },
  box: {
    width: "80%",
    height: "25%",
    backgroundColor: "lightcoral",
    alignContent: 'center',
    padding: 20,
    marginBottom: 10,
  },
  boxContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 20,
  },
  column: {
    flexDirection: 'column',
    flex: 1,
    paddingHorizontal: 10,
    justifyContent: 'space-between',
  },
});

export default LinksPage;
