import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, ScrollView, StyleSheet } from 'react-native';

const SurveyPage = ({ navigation }) => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [occupation, setOccupation] = useState('');
  const [studentLoans, setStudentLoans] = useState(false);
  const [income, setIncome] = useState('');
  const [showResults, setShowResults] = useState(false);

  const handleSurveySubmit = () => {
    // Logic to handle the survey submission and show results
    setShowResults(true);
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.header}>Tax Survey</Text>

      <TextInput
        style={styles.input}
        placeholder="Name"
        onChangeText={text => setName(text)}
        value={name}
      />

      <TextInput
        style={styles.input}
        placeholder="Age"
        onChangeText={text => setAge(text)}
        value={age}
        keyboardType="numeric"
      />

      <View style={styles.occupationContainer}>
        <Text style={styles.label}>Occupation:</Text>
        <TouchableOpacity
          style={[styles.occupationOption, occupation === 'student' && styles.selectedOption]}
          onPress={() => setOccupation('student')}
        >
          <Text>Student</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.occupationOption, occupation === 'other' && styles.selectedOption]}
          onPress={() => setOccupation('other')}
        >
          <Text>Other</Text>
        </TouchableOpacity>
      </View>

      {occupation === 'student' && (
        <View>
          {/* Ask about student loans */}
        </View>
      )}

      {occupation === 'other' && (
        <View>
          {/* Display general info for other occupation */}
        </View>
      )}

      {occupation === 'employed' && (
        <View>
          <Text style={styles.label}>Income:</Text>
          {/* Display income-related options */}
        </View>
      )}

      <TouchableOpacity style={styles.button} onPress={handleSurveySubmit}>
        <Text style={styles.buttonText}>Submit Survey</Text>
      </TouchableOpacity>

      {showResults && (
        <View>
          {/* Display tax filing obligation message + resources - whatever else we want */}
        </View>
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    padding: 16,
    justifyContent: 'center',
    alignItems: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  input: {
    height: 40,
    width: '100%',
    borderColor: '#ccc',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
    borderRadius: 5,
  },
  occupationContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
  },
  occupationOption: {
    flex: 1,
    backgroundColor: '#f2f2f2',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
    marginHorizontal: 5,
  },
  selectedOption: {
    backgroundColor: '#007bff',
  },
  button: {
    backgroundColor: '#007bff',
    paddingVertical: 12,
    paddingHorizontal: 20,
    borderRadius: 8,
    marginTop: 20,
  },
  buttonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});

export default SurveyPage;
