import React, { useState } from 'react';
import { View, Text, CheckBox, ScrollView, StyleSheet, Button } from 'react-native';

const SurveyPage = ({ navigation }) => {
  const [isUnder18, setIsUnder18] = useState(false);
  const [occupation, setOccupation] = useState('');
  const [income, setIncome] = useState('');

  return (
    <ScrollView style={styles.scrollView}>
      <View style={styles.nav}>
        <Button
          title="Survey"
          color="black"
          onPress={() => navigation.navigate('Survey')}
        />
        <Button
          title="Information"
          color="black"
          onPress={() => navigation.navigate('Information')}
        />
        <Button
          title="Tax Fraud"
          color="black"
          onPress={() => navigation.navigate('TaxFraud')}
        />
        <Button
          title="About Us"
          color="black"
          onPress={() => navigation.navigate('AboutUs')}
        />
      </View>

      <View style={styles.container}>
        <Text style={styles.header}>Tax Survey</Text>

        <CheckBox
          style={styles.checkbox}
          value={isUnder18}
          onValueChange={value => setIsUnder18(value)}
        />
        <Text style={styles.label}>Are you under 18?</Text>

        {isUnder18 ? (
          <Text style={styles.infoText}>
            Check Box 5 to see if you are eligible for taxes - you may be a "dependent" on a legal guardian's taxes and do not need to file for taxes.
          </Text>
        ) : (
          <View>
            <Text style={styles.label}>Occupation:</Text>
            <CheckBox
              style={styles.checkbox}
              value={occupation === 'student'}
              onValueChange={() => setOccupation('student')}
            />
            <Text style={styles.checkboxLabel}>Student</Text>

            <CheckBox
              style={styles.checkbox}
              value={occupation === 'other'}
              onValueChange={() => setOccupation('other')}
            />
            <Text style={styles.checkboxLabel}>Other</Text>

            <CheckBox
              style={styles.checkbox}
              value={occupation === 'employed'}
              onValueChange={() => setOccupation('employed')}
            />
            <Text style={styles.checkboxLabel}>Employed</Text>

            {occupation === 'student' && (
              <Text style={styles.infoText}>Find more about student finances and loans in Box 6.</Text>
            )}

            {occupation === 'other' && (
              <Text style={styles.infoText}>Find more general information about taxes in Box 7.</Text>
            )}

            {occupation === 'employed' && (
              <View>
                <Text style={styles.label}>Income:</Text>

                <CheckBox
                  style={styles.checkbox}
                  value={income === 'overCertainAmount'}
                  onValueChange={() => setIncome('overCertainAmount')}
                />
                <Text style={styles.checkboxLabel}>Gross Income over $12,950</Text>

                <CheckBox
                  style={styles.checkbox}
                  value={income === 'underCertainAmount'}
                  onValueChange={() => setIncome('underCertainAmount')}
                />
                <Text style={styles.checkboxLabel}>Gross Income under $12,950</Text>

                {income === 'overCertainAmount' && (
                  <Text style={styles.infoText}>
                    If you are single and make a gross income of over $12,950, it is likely you will need to file for taxes this year. See Box 8 for more information.
                  </Text>
                )}
                {income === 'underCertainAmount' && (
                  <Text style={styles.infoText}>
                    If you are single and make a gross income under $12,950, it is likely you will NOT need to file for taxes this year. See Box 9 for more information.
                  </Text>
                )}
              </View>
            )}
          </View>
        )}
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  scrollView: {
    flex: 1,
    backgroundColor: '#f0f0f0',
  },
  nav: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    padding: 10,
    backgroundColor: 'white',
    borderBottomWidth: 1,
    borderBottomColor: 'black',
  },
  container: {
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    color: 'black',
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginTop: 10,
  },
  checkbox: {
    alignSelf: 'flex-start',
  },
  checkboxLabel: {
    fontSize: 16,
    marginLeft: 10,
  },
  infoText: {
    marginTop: 10,
  },
});

export default SurveyPage;
