const styles = StyleSheet.create({
  screen: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  backgroundContainer: {
    flex: 1,
  },
  body: {
    flex: 2,
    width: '90%', // Adjusted width to give some space on the sides
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'lightblue',
    padding: 20, // Added padding for better spacing
  },
  nav: {
    flex: 0.2,
    justifyContent: 'center',
    flexDirection: 'row',
    alignItems: 'flex-start',
    paddingTop: 20, // Added paddingTop for better spacing
  },
  introTitle: {
    flex: 0.5,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'lightblue',
    paddingVertical: 20, // Adjusted padding for better spacing
  },
  footer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#dcd0f7',
    paddingBottom: 10, // Added paddingBottom for better spacing
  },
  text: {
    textAlign: 'center',
    fontSize: 30,
    fontWeight: 'bold',
  },
  tinyText: {
    textAlign: 'center',
    fontSize: 20,
    paddingHorizontal: 20, // Adjusted paddingHorizontal for better spacing
    lineHeight: 28, // Adjusted lineHeight for better readability
  },
});
