import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, CheckBox, ScrollView, StyleSheet } from 'react-native';

const SurveyPage = ({ navigation }) => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [isUnder18, setIsUnder18] = useState(false);
  const [occupation, setOccupation] = useState('');
  const [studentLoans, setStudentLoans] = useState(false);
  const [income, setIncome] = useState('');
  const [showResults, setShowResults] = useState(false);

  const handleSurveySubmit = () => {
    // Logic to handle the survey submission and show results
    setShowResults(true);
  };

  const handleAgeChange = (text) => {
    setAge(text);
    setIsUnder18(parseInt(text) <= 18);
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.header}>Tax Survey</Text>

      <Text style={styles.label}>Name:</Text>
      <TextInput
        style={styles.input}
        placeholder="Please enter your name"
        onChangeText={text => setName(text)}
        value={name}
      />

      <Text style={styles.label}>Age:</Text>
      <TextInput
        style={styles.input}
        placeholder="Please enter your age"
        onChangeText={handleAgeChange}
        value={age}
        keyboardType="numeric"
      />

      {isUnder18 ? null : (
        <View>
          <View style={styles.checkboxContainer}>
            <CheckBox
              value={occupation === 'student'}
              onValueChange={() => setOccupation('student')}
            />
            <Text>Student</Text>
          </View>
          <View style={styles.checkboxContainer}>
            <CheckBox
              value={occupation === 'other'}
              onValueChange={() => setOccupation('other')}
            />
            <Text>Other</Text>
          </View>

          {occupation === 'student' && (
            <View>
              {/* Ask about student loans */}
            </View>
          )}

          {occupation === 'other' && (
            <View>
              {/* Display general info for other occupation */}
            </View>
          )}

          {occupation === 'employed' && (
            <View>
              <Text style={styles.label}>Income:</Text>
              {/* Display income-related checkboxes */}
            </View>
          )}
        </View>
      )}

      <TouchableOpacity style={styles.button} onPress={handleSurveySubmit}>
        <Text style={styles.buttonText}>Submit Survey</Text>
      </TouchableOpacity>

      {showResults && (
        <View>
          {/* Display tax filing obligation message + resources - whatever else we want */}
        </View>
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    padding: 16,
    justifyContent: 'center',
    alignItems: 'center',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  input: {
    height: 40,
    width: '100%',
    borderColor: '#ccc',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
    borderRadius: 5,
  },
  checkboxContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  button: {
    backgroundColor: '#007bff',
    paddingVertical: 12,
    paddingHorizontal: 20,
    borderRadius: 8,
    marginTop: 20,
  },
  buttonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});

export default SurveyPage;
